args:
/* Help Modal */
.help-content {
    max-width: 700px;
    text-align: left;
}

.help-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 2rem;
}

.help-section h4 {
    color: var(--primary);
    border-bottom: 1px solid var(--border-color);
    padding-bottom: 0.5rem;
    margin-bottom: 1rem;
    font-size: 1.1rem;
}

.help-section.full-width {
    grid-column: 1 / -1;
}

.shortcut-list {
    list-style: none;
    padding: 0;
    margin: 0;
    display: flex;
    flex-direction: column;
    gap: 0.8rem;
}

.shortcut-list.two-col {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 1rem;
}

.shortcut-list li {
    display: flex;
    align-items: center;
    gap: 0.8rem;
    color: var(--text-main);
    font-size: 0.95rem;
}

.key {
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid var(--border-color);
    border-radius: 4px;
    padding: 2px 8px;
    font-family: monospace;
    font-weight: bold;
    color: var(--primary);
    box-shadow: 0 2px 0 rgba(0,0,0,0.5);
    min-width: 24px;
    text-align: center;
    font-size: 0.9rem;
}
