args:
/* Word Choice Timer */
.word-choice-timer {
    font-size: 2rem;
    color: var(--primary);
    font-weight: bold;
    margin-bottom: 1rem;
    font-family: 'Orbitron', sans-serif;
    text-shadow: 0 0 10px var(--primary-glow);
}

/* Modal Slider Item (New Layout) */
.modal-slider-item {
    display: flex;
    flex-direction: column;
    gap: 10px;
    margin-bottom: 5px;
}

.modal-slider-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.modal-slider-label {
    display: flex;
    align-items: center;
    gap: 10px;
    color: var(--text-dim);
    font-weight: bold;
    font-size: 0.8rem;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.modal-slider-label i {
    font-size: 1rem;
    width: 20px;
    text-align: center;
    color: var(--text-dim);
}

.modal-slider-value {
    background: #151515;
    border: 1px solid rgba(255,255,255,0.1);
    padding: 4px 10px;
    border-radius: 6px;
    font-family: 'Orbitron', sans-serif;
    font-size: 0.9rem;
    font-weight: bold;
    min-width: 60px;
    text-align: center;
    color: #e0e0e0;
}

.modal-slider-item input[type="range"] {
    width: 100%;
    height: 12px;
    -webkit-appearance: none;
    appearance: none;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 6px;
    outline: none;
    margin-top: 5px;
}

.modal-slider-item input[type="range"]::-webkit-slider-runnable-track {
    width: 100%;
    height: 12px;
    cursor: pointer;
    background: transparent;
    border-radius: 6px;
}

.modal-slider-item input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    width: 16px;
    height: 16px;
    background: var(--text-dim);
    border-radius: 50%;
    cursor: pointer;
    transition: all 0.2s;
    box-shadow: 0 2px 5px rgba(0,0,0,0.3);
    margin-top: -2px;
}

.modal-slider-item input[type="range"]:hover::-webkit-slider-thumb {
    background: var(--primary);
    transform: scale(1.2);
}

/* Reveal Animation */
.reveal-animation {
    animation: revealPulse 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    color: var(--accent);
    text-shadow: 0 0 15px var(--accent);
}

@keyframes revealPulse {
    0% { transform: scale(1); }
    50% { transform: scale(1.2); }
    100% { transform: scale(1); }
}

/* Segmented Control for Radio Buttons */
.segmented-control {
    display: flex;
    background: rgba(0, 0, 0, 0.3);
    border-radius: var(--radius-md);
    padding: 4px;
    border: 1px solid var(--border-color);
    gap: 4px;
    margin-top: 5px;
}

.segmented-control label {
    flex: 1;
    text-align: center;
    padding: 8px 12px;
    cursor: pointer;
    border-radius: var(--radius-sm);
    transition: all 0.2s ease;
    color: var(--text-dim);
    font-size: 0.9rem;
    font-weight: 600;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 8px;
    user-select: none;
    border: 1px solid transparent;
}

.segmented-control label:hover {
    color: var(--text-main);
    background: rgba(255, 255, 255, 0.05);
}

.segmented-control input[type="radio"] {
    display: none;
}

.segmented-control label:has(input:checked) {
    background: var(--primary);
    color: white;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
    border-color: rgba(255, 255, 255, 0.1);
}

/* Fallback for browsers not supporting :has */
.segmented-control input:checked + span {
    /* This selector logic requires structure adjustment if :has isn't supported, 
       but most modern browsers support it. 
       Alternatively, we can use JS to toggle a class, but CSS :has is cleaner.
       For safety, let's assume :has works or the user is on a modern browser.
       If not, the standard radio button behavior (hidden) might be an issue.
       Let's stick to :has for now as it's widely supported in 2025.
    */
}

/* Roulette Modal */
.roulette-content {
    text-align: center;
    max-width: 500px;
    background: linear-gradient(135deg, #2c3e50, #1a252f);
    border: 2px solid var(--primary);
    box-shadow: 0 0 30px rgba(0, 0, 0, 0.5), 0 0 10px var(--primary);
}

.roulette-machine {
    margin: 2rem 0;
    padding: 10px;
    background: #34495e;
    border-radius: 10px;
    box-shadow: inset 0 0 15px rgba(0,0,0,0.5);
    border: 4px solid #f1c40f;
    position: relative;
}

.roulette-window {
    height: 100px;
    overflow: hidden;
    position: relative;
    background: white;
    border-radius: 4px;
}

.roulette-strip {
    display: flex;
    flex-direction: column;
    align-items: center;
    /* Will be animated via JS transform */
}

.roulette-item {
    height: 100px;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 2rem;
    font-weight: bold;
    color: #2c3e50;
    text-transform: uppercase;
    font-family: 'Orbitron', sans-serif;
}

.roulette-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(to bottom, rgba(0,0,0,0.6) 0%, rgba(0,0,0,0) 20%, rgba(0,0,0,0) 80%, rgba(0,0,0,0.6) 100%);
    pointer-events: none;
}

.roulette-line {
    position: absolute;
    top: 50%;
    left: 0;
    right: 0;
    height: 4px;
    background: rgba(231, 76, 60, 0.7);
    transform: translateY(-50%);
    box-shadow: 0 0 5px rgba(231, 76, 60, 0.8);
    pointer-events: none;
}

.roulette-result {
    margin-top: 1rem;
    animation: popIn 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.winner-label {
    font-size: 1.2rem;
    color: var(--text-dim);
    margin-bottom: 0.5rem;
}

.winner-word {
    font-size: 3rem;
    font-weight: bold;
    color: var(--success);
    text-shadow: 0 0 20px rgba(46, 204, 113, 0.4);
    font-family: 'Orbitron', sans-serif;
    text-transform: uppercase;
}
