args:
/* Word Choice Timer */
.word-choice-timer {
    font-size: 2rem;
    color: var(--primary);
    font-weight: bold;
    margin-bottom: 1rem;
    font-family: 'Orbitron', sans-serif;
    text-shadow: 0 0 10px var(--primary-glow);
}

/* Modal Slider Item (New Layout) */
.modal-slider-item {
    display: flex;
    flex-direction: column;
    gap: 10px;
    margin-bottom: 5px;
}

.modal-slider-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.modal-slider-label {
    display: flex;
    align-items: center;
    gap: 10px;
    color: var(--text-dim);
    font-weight: bold;
    font-size: 0.8rem;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.modal-slider-label i {
    font-size: 1rem;
    width: 20px;
    text-align: center;
    color: var(--text-dim);
}

.modal-slider-value {
    background: #151515;
    border: 1px solid rgba(255,255,255,0.1);
    padding: 4px 10px;
    border-radius: 6px;
    font-family: 'Orbitron', sans-serif;
    font-size: 0.9rem;
    font-weight: bold;
    min-width: 60px;
    text-align: center;
    color: #e0e0e0;
}

.modal-slider-item input[type="range"] {
    width: 100%;
    height: 12px;
    -webkit-appearance: none;
    appearance: none;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 6px;
    outline: none;
    margin-top: 5px;
}

.modal-slider-item input[type="range"]::-webkit-slider-runnable-track {
    width: 100%;
    height: 12px;
    cursor: pointer;
    background: transparent;
    border-radius: 6px;
}

.modal-slider-item input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    width: 16px;
    height: 16px;
    background: var(--text-dim);
    border-radius: 50%;
    cursor: pointer;
    transition: all 0.2s;
    box-shadow: 0 2px 5px rgba(0,0,0,0.3);
    margin-top: -2px;
}

.modal-slider-item input[type="range"]:hover::-webkit-slider-thumb {
    background: var(--primary);
    transform: scale(1.2);
}
