/* SVG Gradients Application */
#tool-pen svg { stroke: url(#grad-pen); }
#tool-airbrush svg { stroke: url(#grad-airbrush); }
#tool-fill svg { stroke: url(#grad-fill); }
#tool-smudge svg { stroke: url(#grad-smudge); }
#tool-eraser svg { stroke: url(#grad-eraser); }
#tool-pipette svg { stroke: url(#grad-pipette); }
#tool-selection svg { stroke: url(#grad-selection); }

#tool-rect svg { stroke: url(#grad-rect); }
#tool-circle svg { stroke: url(#grad-circle); }
#tool-triangle svg { stroke: url(#grad-triangle); }
#tool-line svg { stroke: url(#grad-line); }

#btn-undo svg { stroke: url(#grad-undo); }
#btn-redo svg { stroke: url(#grad-redo); }
#btn-help svg { stroke: url(#grad-help); }

/* Self Player Highlight */
.player-card.is-me .name-text {
    color: var(--primary);
    text-shadow: 0 0 12px var(--primary-glow);
}

.player-card.is-me {
    background: rgba(255, 255, 255, 0.03);
    border: 1px solid rgba(255, 255, 255, 0.05);
}

/* Enhanced Chat Names */
.message-name {
    font-family: 'Orbitron', sans-serif;
    letter-spacing: 0.5px;
    color: var(--primary);
    text-shadow: 0 0 5px rgba(99, 102, 241, 0.3);
}

/* Art Deco Theme for Modals */
.modal-content {
    border: 1px solid var(--deco-gold) !important;
    box-shadow: 0 0 30px rgba(0, 0, 0, 0.8), 
                inset 0 0 20px rgba(0, 0, 0, 0.5),
                0 0 15px var(--deco-gold-glow) !important;
    position: relative;
    outline: 1px solid rgba(212, 175, 55, 0.2);
    outline-offset: -8px;

    /* Corner Accents */
    background: 
        /* Top Left - Vertical & Horizontal */
        linear-gradient(to right, var(--deco-gold) 3px, transparent 3px) 0 0 / 25px 25px no-repeat,
        linear-gradient(to bottom, var(--deco-gold) 3px, transparent 3px) 0 0 / 25px 25px no-repeat,

        /* Top Right - Vertical & Horizontal */
        linear-gradient(to left, var(--deco-gold) 3px, transparent 3px) 100% 0 / 25px 25px no-repeat,
        linear-gradient(to bottom, var(--deco-gold) 3px, transparent 3px) 100% 0 / 25px 25px no-repeat,

        /* Bottom Left - Vertical & Horizontal */
        linear-gradient(to right, var(--deco-gold) 3px, transparent 3px) 0 100% / 25px 25px no-repeat,
        linear-gradient(to top, var(--deco-gold) 3px, transparent 3px) 0 100% / 25px 25px no-repeat,

        /* Bottom Right - Vertical & Horizontal */
        linear-gradient(to left, var(--deco-gold) 3px, transparent 3px) 100% 100% / 25px 25px no-repeat,
        linear-gradient(to top, var(--deco-gold) 3px, transparent 3px) 100% 100% / 25px 25px no-repeat,

        /* Base Background */
        var(--bg-glass-strong);
}

/* Center Diamond Decorations */
.modal-content::before {
    content: '';
    position: absolute;
    top: -7px;
    left: 50%;
    transform: translateX(-50%) rotate(45deg);
    width: 14px;
    height: 14px;
    background: var(--bg-dark);
    border: 2px solid var(--deco-gold);
    box-shadow: 0 0 8px var(--deco-gold-glow);
    z-index: 10;
}

.modal-content::after {
    content: '';
    position: absolute;
    bottom: -7px;
    left: 50%;
    transform: translateX(-50%) rotate(45deg);
    width: 14px;
    height: 14px;
    background: var(--bg-dark);
    border: 2px solid var(--deco-gold);
    box-shadow: 0 0 8px var(--deco-gold-glow);
    z-index: 10;
}

/* Adjust Modal Header to match theme */
.modal-header {
    border-bottom: 1px solid rgba(212, 175, 55, 0.2);
}

.modal-header h3 {
    color: var(--deco-gold);
    text-shadow: 0 0 5px var(--deco-gold-glow);
    font-family: 'Orbitron', sans-serif;
    letter-spacing: 1px;
}

/* Close button hover effect */
.close-btn:hover {
    color: var(--deco-gold);
    text-shadow: 0 0 8px var(--deco-gold-glow);
}

/* Art Deco Theme for Modals - Simplified */
.modal-content {
    /* Single thin elegant border */
    border: 1px solid var(--deco-gold) !important;

    /* Softer shadow, less aggressive */
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.6), 
                0 0 10px rgba(212, 175, 55, 0.15) !important;

    position: relative;

    /* Clean background without corner gradients */
    background: var(--bg-glass-strong);

    /* Ensure rounded corners are respected */
    border-radius: var(--radius-lg);
}

/* Center Diamond Decorations - Refined */
.modal-content::before {
    content: '';
    position: absolute;
    top: -6px; /* Half of height (12px) to center on line */
    left: 50%;
    transform: translateX(-50%) rotate(45deg);
    width: 12px;
    height: 12px;
    background: var(--bg-dark);
    border: 1px solid var(--deco-gold);
    box-shadow: 0 0 5px var(--deco-gold-glow);
    z-index: 10;
}

.modal-content::after {
    content: '';
    position: absolute;
    bottom: -6px;
    left: 50%;
    transform: translateX(-50%) rotate(45deg);
    width: 12px;
    height: 12px;
    background: var(--bg-dark);
    border: 1px solid var(--deco-gold);
    box-shadow: 0 0 5px var(--deco-gold-glow);
    z-index: 10;
}

/* Adjust Modal Header */
.modal-header {
    border-bottom: 1px solid rgba(212, 175, 55, 0.15); /* More subtle */
}

.modal-header h3 {
    color: var(--deco-gold);
    text-shadow: 0 0 10px rgba(212, 175, 55, 0.3); /* Softer glow */
    font-family: 'Orbitron', sans-serif;
    letter-spacing: 1px;
}

/* Close button */
.close-btn:hover {
    color: var(--deco-gold);
    text-shadow: 0 0 5px var(--deco-gold-glow);
}

/* Result Modal - Larger */
.result-content {
    max-width: 800px !important;
    width: 90%;
    max-height: 90vh;
    display: flex;
    flex-direction: column;
}

.result-scores {
    max-height: 60vh;
    overflow-y: auto;
    flex: 1;
}

/* Movable Toolbar Styles */
.drag-handle {
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0 10px;
    color: var(--text-dim);
    cursor: grab;
    border-right: 1px solid rgba(255, 255, 255, 0.1);
    margin-right: 10px;
    transition: color 0.2s;
}

.drag-handle:hover {
    color: var(--primary);
}

.drag-handle:active {
    cursor: grabbing;
}

.toolbar.dragging {
    cursor: grabbing;
    transition: none; /* Disable transition during drag for smoothness */
    box-shadow: 0 25px 60px rgba(0, 0, 0, 0.8);
    border-color: var(--primary);
}

/* Ensure toolbar can be positioned freely */
.toolbar.custom-pos {
    transform: none; /* Remove centering transform */
    bottom: auto; /* Reset bottom if top is set */
}

/* Right Side Sidebar Style */
.sidebar-col-2.right-side {
    border-right: none;
    border-left: 1px solid rgba(255, 255, 255, 0.08);
    box-shadow: -5px 0 30px rgba(0, 0, 0, 0.2);
}

/* Icon Button */
.icon-btn {
    background: transparent;
    border: none;
    color: var(--text-dim);
    cursor: pointer;
    padding: 4px 8px;
    border-radius: 4px;
    transition: all 0.2s;
}

.icon-btn:hover {
    color: var(--primary);
    background: rgba(255, 255, 255, 0.1);
}

/* Updated Drag Handle Style */
.drag-handle {
    border-right: none;
    margin-right: 0;
    padding: 0 15px;
    border-left: 1px solid rgba(255, 255, 255, 0.1);
    border-right: 1px solid rgba(255, 255, 255, 0.1);
}

/* Updated Sliders Row Layout */
.sliders-row {
    margin-bottom: 15px;
    padding-bottom: 10px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.sliders-row .slider-row {
    flex: 1;
    min-width: 150px;
}

/* Slider Labels */
.slider-row {
    flex-direction: column;
    align-items: stretch;
    gap: 5px;
}

.slider-header {
    display: flex;
    align-items: center;
    gap: 8px;
    padding-left: 2px;
}

.slider-label {
    font-size: 0.65rem;
    color: var(--text-dim);
    text-transform: uppercase;
    font-weight: 600;
    letter-spacing: 0.5px;
}

/* Disable transition when dragging */
.toolbar.dragging {
    transition: none !important;
}

/* Floating Window */
.floating-window {
    position: absolute;
    top: 100px;
    left: 100px;
    width: 400px;
    height: 500px;
    background: rgba(18, 18, 24, 0.95);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 8px;
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.5);
    display: flex;
    flex-direction: column;
    z-index: 1000;
    backdrop-filter: blur(10px);
    overflow: hidden;
    resize: both;
}

.floating-window.hidden {
    display: none;
}

.window-header {
    padding: 10px;
    background: rgba(255, 255, 255, 0.05);
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: grab;
    user-select: none;
}

.window-header:active {
    cursor: grabbing;
}

.window-title {
    font-weight: bold;
    color: var(--text-main);
    display: flex;
    align-items: center;
    gap: 8px;
}

.window-controls {
    display: flex;
    gap: 5px;
}

.window-btn {
    background: transparent;
    border: none;
    color: var(--text-dim);
    cursor: pointer;
    padding: 4px;
    border-radius: 4px;
    transition: all 0.2s;
}

.window-btn:hover {
    color: white;
    background: rgba(255, 255, 255, 0.1);
}

.browser-toolbar {
    padding: 8px;
    display: flex;
    gap: 8px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.05);
}

#browser-url {
    flex: 1;
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid rgba(255, 255, 255, 0.1);
    color: white;
    padding: 6px 10px;
    border-radius: 4px;
    font-size: 0.9rem;
}

#btn-browser-go {
    background: var(--primary);
    border: none;
    color: white;
    padding: 0 12px;
    border-radius: 4px;
    cursor: pointer;
}

.browser-content {
    flex: 1;
    background: white;
    position: relative;
}

#browser-frame {
    width: 100%;
    height: 100%;
    border: none;
    display: block;
}

.browser-status {
    padding: 4px 8px;
    font-size: 0.75rem;
    color: var(--text-dim);
    background: rgba(0, 0, 0, 0.2);
    text-align: center;
}

/* Mobile-like Floating Window */
.floating-window {
    width: 375px; /* iPhone width */
    height: 600px;
    border-radius: 20px; /* More rounded corners */
    border: 4px solid #333; /* Bezel look */
}

.browser-content {
    border-bottom-left-radius: 16px;
    border-bottom-right-radius: 16px;
    overflow: hidden;
}

.browser-nav-btn {
    background: transparent;
    border: none;
    color: var(--text-dim);
    cursor: pointer;
    padding: 0 8px;
    border-radius: 4px;
    transition: all 0.2s;
}

.browser-nav-btn:hover {
    color: white;
    background: rgba(255, 255, 255, 0.1);
}

/* Tracing Image */
.tracing-image {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: contain;
    z-index: 1;
    pointer-events: none;
}

/* Tracing Controls */
.tracing-controls {
    position: absolute;
    z-index: 15; /* Above canvas (10) */
    pointer-events: none; /* Let clicks pass through body */
    border: 1px dashed rgba(99, 102, 241, 0.5);
    box-sizing: border-box;
}

.resize-handle {
    position: absolute;
    width: 12px;
    height: 12px;
    background: white;
    border: 1px solid var(--primary);
    border-radius: 50%;
    pointer-events: auto; /* Catch clicks */
    z-index: 16;
    transition: transform 0.1s;
}

.resize-handle:hover {
    transform: scale(1.2);
    background: var(--primary);
}

.resize-handle.nw { top: -6px; left: -6px; cursor: nw-resize; }
.resize-handle.ne { top: -6px; right: -6px; cursor: ne-resize; }
.resize-handle.sw { bottom: -6px; left: -6px; cursor: sw-resize; }
.resize-handle.se { bottom: -6px; right: -6px; cursor: se-resize; }

