/* Move Handle */
.move-handle {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 32px;
    height: 32px;
    background: rgba(255, 255, 255, 0.2);
    border: 1px solid var(--primary);
    border-radius: 50%;
    cursor: move;
    pointer-events: auto;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    backdrop-filter: blur(4px);
    transition: all 0.2s;
    z-index: 20;
}

.move-handle:hover {
    background: var(--primary);
    transform: translate(-50%, -50%) scale(1.1);
    box-shadow: 0 0 10px var(--primary-glow);
}

/* Layer Opacity Slider */
.layer-opacity-slider {
    width: 100%;
    height: 4px;
    -webkit-appearance: none;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 2px;
    outline: none;
    margin-top: 4px;
}

.layer-opacity-slider::-webkit-slider-thumb {
    -webkit-appearance: none;
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: var(--primary);
    cursor: pointer;
    transition: transform 0.1s;
}

.layer-opacity-slider::-webkit-slider-thumb:hover {
    transform: scale(1.2);
}

/* Finalize Tracing Button */
.tracing-finalize-btn {
    position: fixed;
    bottom: 100px; /* Above toolbar */
    left: 50%;
    transform: translateX(-50%);
    background: var(--success);
    color: white;
    border: none;
    padding: 10px 20px;
    border-radius: 30px;
    font-weight: bold;
    font-size: 1rem;
    cursor: pointer;
    box-shadow: 0 4px 15px rgba(0,0,0,0.3);
    z-index: 1000;
    display: flex;
    align-items: center;
    gap: 8px;
    transition: all 0.2s;
    animation: slideUp 0.3s ease-out;
    text-transform: uppercase;
    letter-spacing: 1px;
    font-family: 'Orbitron', sans-serif;
}

.tracing-finalize-btn:hover {
    background: #22c55e; /* Success hover */
    transform: translateX(-50%) scale(1.05);
    box-shadow: 0 6px 20px rgba(0,0,0,0.4);
}

@keyframes slideUp {
    from { transform: translate(-50%, 20px); opacity: 0; }
    to { transform: translate(-50%, 0); opacity: 1; }
}

/* Ensure canvas wrapper clips content */
.canvas-wrapper {
    overflow: hidden;
}

/* Use as Layer Button Style */
.use-as-layer-btn {
    align-self: flex-start;
    background: var(--success);
    border: none;
    color: white;
    padding: 8px 16px;
    border-radius: 4px;
    cursor: pointer;
    display: flex;
    align-items: center;
    gap: 8px;
    font-size: 0.9rem;
    font-weight: 600;
    box-shadow: 0 2px 5px rgba(0,0,0,0.2);
    transition: all 0.2s;
}

.use-as-layer-btn:hover {
    background: #22c55e; /* Success hover */
    transform: translateY(-1px);
    box-shadow: 0 4px 8px rgba(0,0,0,0.3);
}

/* Disabled Slider Style */
.disabled-slider {
    opacity: 0.3;
    pointer-events: none;
    filter: grayscale(100%);
}

.disabled-slider input[type=range]::-webkit-slider-thumb {
    opacity: 0;
    visibility: hidden;
}

.disabled-slider input[type=range]::-moz-range-thumb {
    opacity: 0;
    visibility: hidden;
}

/* Disabled State for Tool Buttons */
.tool-btn:disabled,
.tool-btn[disabled] {
    opacity: 0.2 !important;
    cursor: not-allowed;
    pointer-events: none;
    filter: grayscale(100%);
    user-select: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
}

.tool-btn:disabled .tool-label,
.tool-btn[disabled] .tool-label {
    user-select: none;
}
